import React, { useState } from 'react';
import { createTask } from '../api';
import { useNavigate } from 'react-router-dom';

const AddTask = () => {
    const [task, setTask] = useState({ title: '', description: '' });
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        await createTask(task);
        navigate('/');
    };

    return (
        <div>
            <h2>Add Task</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Task Title" value={task.title} onChange={(e) => setTask({ ...task, title: e.target.value })} required />
                <textarea placeholder="Task Description" value={task.description} onChange={(e) => setTask({ ...task, description: e.target.value })} required />
                <button type="submit">Add Task</button>
            </form>
        </div>
    );
};

export default AddTask;
