import React, { useState } from 'react';
import { createTask } from '../api';
import { useNavigate } from 'react-router-dom';

const TaskForm = () => {
    const navigate = useNavigate();
    const [task, setTask] = useState({
        title: '',
        description: '',
        category: 'Other',
        priority: 'Medium',
        dueDate: '',
        completed: false
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        await createTask(task);
        navigate('/');
    };

    return (
        <div className="task-form-container">
            <h2>Add New Task</h2>
            <form onSubmit={handleSubmit} className="task-form">
                {/* Task Title */}
                <label>Title:</label>
                <input
                    type="text"
                    placeholder="Task Title"
                    value={task.title}
                    onChange={(e) => setTask({ ...task, title: e.target.value })}
                    required
                />

                {/* Task Description */}
                <label>Description:</label>
                <textarea
                    placeholder="Task Description"
                    value={task.description}
                    onChange={(e) => setTask({ ...task, description: e.target.value })}
                    required
                />

                {/* Task Category */}
                <label>Category:</label>
                <select value={task.category} onChange={(e) => setTask({ ...task, category: e.target.value })}>
                    <option value="Work">Work</option>
                    <option value="Personal">Personal</option>
                    <option value="Shopping">Shopping</option>
                    <option value="Other">Other</option>
                </select>

                {/* Task Priority */}
                <label>Priority:</label>
                <select value={task.priority} onChange={(e) => setTask({ ...task, priority: e.target.value })}>
                    <option value="Low">Low</option>
                    <option value="Medium">Medium</option>
                    <option value="High">High</option>
                </select>

                {/* Due Date */}
                <label>Due Date:</label>
                <input type="date" value={task.dueDate} onChange={(e) => setTask({ ...task, dueDate: e.target.value })} />

                {/* Task Completion Checkbox */}
                <label>
                    <input type="checkbox" checked={task.completed} onChange={(e) => setTask({ ...task, completed: e.target.checked })} />
                    Mark as Completed
                </label>

                {/* Submit Button */}
                <button type="submit" className="btn-submit">Add Task</button>
            </form>
        </div>
    );
};

export default TaskForm;
