import React, { useEffect, useState } from 'react';
import { getTasks, updateTask } from '../api';
import { useNavigate, useParams } from 'react-router-dom';

const EditTask = () => {
    const { id } = useParams();
    const [task, setTask] = useState({ title: '', description: '' });
    const navigate = useNavigate();

    useEffect(() => {
        fetchTask();
    }, []);

    const fetchTask = async () => {
        const { data } = await getTasks();
        const currentTask = data.find(task => task._id === id);
        setTask(currentTask || { title: '', description: '' });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await updateTask(id, task);
        navigate('/');
    };

    return (
        <div>
            <h2>Edit Task</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" value={task.title} onChange={(e) => setTask({ ...task, title: e.target.value })} required />
                <textarea value={task.description} onChange={(e) => setTask({ ...task, description: e.target.value })} required />
                <button type="submit">Update Task</button>
            </form>
        </div>
    );
};

export default EditTask;
